cast('VARIABLE' as varchar(9)), 
v.published, NULL, cast('Y' as char(1)) 
FROM sysibm.sysvariables as v,  sysibm.sysdatatypes as d, sysibm.sysmodules as m 
WHERE v.typeid = d.typeid 
