CREATE VIEW "SYSCAT"."ROLES" ("ROLENAME", "ROLEID", "CREATE_TIME", "AUDITPOLICYID", "AUDITPOLICYNAME", "AUDITEXCEPTIONENABLED", "REMARKS") AS
