CREATE VIEW "SYSCAT"."ROUTINEAUTH" ("GRANTOR", "GRANTORTYPE", "GRANTEE", "GRANTEETYPE", "SCHEMA", "SPECIFICNAME", "TYPESCHEMA", "TYPENAME", "ROUTINETYPE", "EXECUTEAUTH", "GRANT_TIME") AS
