CREATE VIEW "SYSCAT"."TABCONST" ("CONSTNAME", "TABSCHEMA", "TABNAME", "OWNER", "OWNERTYPE", "TYPE", "ENFORCED", "TRUSTED", "CHECKEXISTINGDATA", "ENABLEQUERYOPT", "DEFINER", "PERIODNAME", "PERIODPOLICY", "REMARKS") AS
