CREATE VIEW "SYSCAT"."TRANSFORMS" ("TYPEID", "TYPESCHEMA", "TYPENAME", "GROUPNAME", "FUNCID", "FUNCSCHEMA", "FUNCNAME", "SPECIFICNAME", "TRANSFORMTYPE", "FORMAT", "MAXLENGTH", "ORIGIN", "REMARKS") AS
